package package1;

public class TestBoolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a;
		boolean b;
		boolean c;
		
		a = true;
		b = false;
		
		c = a || b; // OR operation
		System.out.println(c);
		
		c = a && b; // AND operation
		System.out.println(c);
		
		c = !a; // NOT operation
		System.out.println(c);
		
	}

}
